svg.script:
const svgElement = document.getElementById('numberCircle');
const circleElement = document.getElementById('circle');
const numberElement = document.getElementById('number');

// Function to adjust the circle radius based on the text width
function adjustCircleRadius(text) {
  const textWidth = numberElement.getComputedTextLength();
  const radius = textWidth / 2 + 8; // Add 8 to account for some padding
  circleElement.setAttribute('r', radius);
  circleElement.setAttribute('cx', radius + 4); // Adjust the circle's center based on the radius
  circleElement.setAttribute('cy', 32); // Keep the circle's vertical center
}

// Initial adjustment
adjustCircleRadius(numberElement.textContent);

// Adjustment on text change
numberElement.addEventListener('DOMCharacterDataModified', () => {
  adjustCircleRadius(numberElement.textContent);
});
